function run(identidadeDoContato) {
    const source = identidadeDoContato.split('@')[1].split('.')[0];
    switch (source) {
        case 'wa':
            return "whatsapp";
            break;
        case '0mn':
            return "blipchat";
            break;
        case 'messenger':
            return "messenger";
            break;
        case 'telegram':
            return "telegram";
            break;
        case 'instagram':
            return "instagram";
            break;
        case 'mailgun':
            return "email";
            break;
        case 'workplace':
            return "workchat";
            break;
        default:
            return "outros"
            break;
    }
}

