function run() {
    let quebrarLinha = { 'simples': '', 'dupla': '' };
    try {
        var resource = /^[0-9]+$/.test("{{contact.identity}}".split('@')[0]);
        switch (resource) {
            case true:
                return { 'simples': '\n', 'dupla': '\n\n' };
            case false:
                return { 'simples': '<br>', 'dupla': '<br><br>' };;
            default:
                return quebrarLinha;
        }
    }
    catch (ex) {
        return quebrarLinha;
    }
}

