const INPUT_TYPES_ACCEPTED = ['text/plain'];

function isValidInputType(inputType) {
	return INPUT_TYPES_ACCEPTED.some(type => type.toLowerCase() === inputType.toString().toLowerCase());
}

function run(inputType) {
    return isValidInputType(inputType);
}

